mapper.sql:
SELECT B.* FROM (
SELECT *
FROM T_BID_PROJ_OFF_SPLR_RLT
WHERE SPLR_ID = #{splrId,jdbcType=DECIMAL}
AND EFF_FLG = '1'
) A
LEFT JOIN (
SELECT *
FROM T_BID_PROJ_OFF
WHERE 1 = 1

    AND ORG_ID IN
    ()


    AND ORG_ID NOT IN
    ()


    AND PROJ_ID = #{projId,jdbcType=DECIMAL}


    AND PROJ_NBR LIKE '%'|| #{projNbr,jdbcType=VARCHAR} ||'%'


    AND PROJ_NAM LIKE '%'|| #{projNam,jdbcType=VARCHAR} ||'%'


    AND PROJ_RSPS = #{projRsps,jdbcType=VARCHAR}


    AND ORG_ID = #{orgId,jdbcType=DECIMAL}


    AND BID_DPT_NAM = #{bidDptNam,jdbcType=VARCHAR}


    AND BID_DPT_ADDR = #{bidDptAddr,jdbcType=VARCHAR}


    AND BID_DPST = #{bidDpst,jdbcType=DECIMAL}


    AND BID_DPST >= #{bidDpstFrom,jdbcType=DECIMAL}


    AND BID_DPST <= #{bidDpstTo,jdbcType=DECIMAL}


    AND CTCT = #{ctct,jdbcType=VARCHAR}


    AND CTCT_TEL = #{ctctTel,jdbcType=VARCHAR}


    AND BID_END_TIM = #{bidEndTim,jdbcType=TIMESTAMP}


    AND BID_END_TIM >= to_timestamp('${bidEndTimFrom,jdbcType=VARCHAR}','yyyy-mm-dd hh24:mi:ss')


    AND BID_END_TIM <= to_timestamp('${bidEndTimTo,jdbcType=VARCHAR}','yyyy-mm-dd hh24:mi:ss')


    AND PROJ_MEMO = #{projMemo,jdbcType=VARCHAR}


    AND QOT_CNT_TYP = #{qotCntTyp,jdbcType=CHAR}


    AND BID_RNG_TYP = #{bidRngTyp,jdbcType=CHAR}


    AND MAT_TYP = #{matTyp,jdbcType=VARCHAR}


    AND PROJ_STS = #{projSts,jdbcType=CHAR}


    AND PROJ_STS in

        #{item}



    AND STS_UPD_TIM = #{stsUpdTim,jdbcType=TIMESTAMP}


    AND BPM_ID = #{bpmId,jdbcType=VARCHAR}


    AND BID_NTC_PUB_FLG = #{bidNtcPubFlg,jdbcType=CHAR}


    AND BID_RST_PUB_FLG = #{bidRstPubFlg,jdbcType=CHAR}


    AND BID_BOK = #{bidbok,jdbcType=clob}


    AND AWD_MEMO = #{awdMemo,jdbcType=VARCHAR}


    AND AWD_INFO = #{awdInfo,jdbcType=clob}


    AND SPLR_IDS = #{splrIds,jdbcType=VARCHAR}


    AND NTC_PUB_TIM = #{ntcPubTim,jdbcType=TIMESTAMP}


    AND RST_PUB_TIM = #{rstPubTim,jdbcType=TIMESTAMP}


    AND RPL_MEMO = #{rplMemo,jdbcType=VARCHAR}


    AND BPM_BUD_SEQ = #{bpmBudSeq,jdbcType=VARCHAR}


    AND BPM_FIN_SEQ = #{bpmFinSeq,jdbcType=VARCHAR}


    AND BPM_RPL_SEQ = #{bpmRplSeq,jdbcType=VARCHAR}


    AND BPM_BUD_ADT_MEMO = #{bpmBudAdtMemo,jdbcType=VARCHAR}


    AND BPM_FIN_ADT_MEMO = #{bpmFinAdtMemo,jdbcType=VARCHAR}


    AND BPM_RPL_ADT_MEMO = #{bpmRplAdtMemo,jdbcType=VARCHAR}


    AND CURR_TYP = #{currTyp,jdbcType=VARCHAR}


    AND DPST_FLG = #{dpstFlg,jdbcType=CHAR}


    AND PUB_PRI_FLG = #{pubPriFlg,jdbcType=CHAR}


    AND SUT_ORG_IDS = #{sutOrgIds,jdbcType=VARCHAR}


    AND MAT_REQ_DEPT = #{matReqDept,jdbcType=VARCHAR}


    AND BID_TEAM_MMB = #{bidTeamMmb,jdbcType=VARCHAR}


    AND MAT_REQ_BPM_APP_SEQ = #{matReqBpmAppSeq,jdbcType=VARCHAR}


    AND CASE_REPT = #{caseRept,jdbcType=VARCHAR}


    AND DEPT = #{dept,jdbcType=VARCHAR}


    AND MAT_PCHS_BGT = #{matPchsBgt,jdbcType=VARCHAR}


    AND BID_TOL_AMT = #{bidTolAmt,jdbcType=VARCHAR}


    AND BRF_DESC = #{brfDesc,jdbcType=VARCHAR}

) B ON A.PROJ_ID = B.PROJ_ID
WHERE B.PROJ_ID IS NOT NULL

