    AND MOD_USR = #{modUsr,jdbcType=VARCHAR}


    AND MOD_TIM = #{modTim,jdbcType=TIMESTAMP}


    AND BID_END_TIM >= to_timestamp('${bidEndTimFrom,jdbcType=VARCHAR}','yyyy-mm-dd hh24:mi:ss')


    AND BID_END_TIM <= to_timestamp('${bidEndTimTo,jdbcType=VARCHAR}','yyyy-mm-dd hh24:mi:ss')


    AND CURR_TYP = #{currTyp,jdbcType=VARCHAR}


    AND DPST_FLG = #{dpstFlg,jdbcType=CHAR}


    AND PUB_PRI_FLG = #{pubPriFlg,jdbcType=CHAR}


    AND PROJ_STS in

        #{item}



    AND CRT_TIM >= to_timestamp(#{crtTimBgnStr,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss')


    AND CRT_TIM <= to_timestamp(#{crtTimEndStr,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss')


    AND SUT_ORG_IDS = #{sutOrgIds,jdbcType=VARCHAR}


    AND GRD_RUL = #{grdRul,jdbcType=CHAR}


    AND PROJ_SUPV = #{projSupv,jdbcType=VARCHAR}


    AND PROJ_SUPV_TEL = #{projSupvTel,jdbcType=VARCHAR}


    ORDER BY PROJ_ID DESC

